import React, { useState } from 'react';

const App = () => {
    const a = new Date().toLocaleTimeString();
    const [time , update] = useState(a);

    const UpdateTime = () => {
        a = new Date().toLocaleTimeString();
        update(a);
    }; 

    setInterval(UpdateTime , 1000);

    return(
        <>
            <div className="ab">
                <h1>{time}</h1>
            </div>
        </>
    );
}

export default App;
