import React, { useState } from 'react';

function App()
{
    const color = "#8e44ad";
    const [bg , setbg ] = useState(color);

    const [btncol , setbtnbg ] = useState('#9b59b6');

    const onClk = () =>{
        const color = "#008080";
        setbg(color);
        setbtnbg('blue')
    }

    return (
        <>
            <div className='div' style={{backgroundColor: bg}}>
                <button onClick={onClk} style={{backgroundColor:btncol}}>Click Me</button>
            </div>
        </>
    );
}

export default App;
