import React, { useState } from 'react';
import './index.css';



const App = () => {
    const a = new Date;
const b = a.toLocaleTimeString();
    const state = useState();
    const [time1 , update1 ] = useState(b);

    const clk = (b) => {
        return(
            update1 = (b)=>{
                time1 = b;
                console.log(time1);
            }
        );
    }

    return (
        <>
            <div className='ab'>
                <h1>{time1}</h1>
                <button type="button" onclick={clk}>Get Time</button>
            </div>
        </>
    );
}

export default App;
