import React, { useState } from 'react';

const App = () => {
    const a = new Date().toLocaleTimeString();
    const [time , update] = useState(a);

    const onclk = () => {
        a = new Date().toLocaleTimeString();
        update(a);
    };

    return(
        <>
            <div className="ab">
                <h1>{time}</h1>
                <button type='button' onClick={onclk}>Get Time</button>
            </div>
        </>
    );
}

export default App;
