import React, { useState } from 'react';

const App = () => {
    const a = new Date().toLocaleTimeString();
    const [time , updte] = useState(a);

    const b = () => {
        a = new Date().toLocaleTimeString();
        updte(a);
    };

    // setInterval( , 1000);

    return (
        <>
            <h1>{time}</h1>
            <button onClick={b}>click</button>
        </>
    );
}

export default App;
