import React, { useState } from 'react';
import './index.css';

const a = new Date;
const App = () => {
    const state = useState();
    const [time1 , update1 ] = useState(a.toLocaleTimeString());

    const clk = () => {
        
            update1(a.toLocaleTimeString());
        
    }

    return (
        <>
            <div className='ab'>
                <h1>{time1}</h1>
                <button type="button" onclick={clk}>Click Me</button>
            </div>
        </>
    );
}

export default App;
