import React, { useState } from 'react';

const App = () => 
{
    const [fullname , setfullname]= useState({
        fname : '',
        lname : ''
    } );

    const inputevent = (event) => {
        console.log(event.target.value);
    }

    const onclk = (a) =>{
        a.preventDefault();
    }

    return(
        <>
        <div style={{textAlign:'center'}}>
        <form onSubmit={onclk}>
            <h1>
                {`Hi ${fname} ${lname}`}
            </h1>
            <input type='text' placeholder='Enter your fname...' onChange={inputevent} name='n_name'/>
            <br/><br/>
            <input type='text' placeholder='Enter your email...' onChange={inputevent} name='n_email'/>
            <br/><br/>
            <button type='submit'>Submit</button>
        </form>
        </div>                        
        </>
    );
}

export default App;
